void SetAnalyseAndReturnBalance(Stack* stack, std::string str, int expectedVal) {
    stack->SetStackString(str);
    EXPECT_TRUE(stack->GetStackBalance() == 0);
    stack->AnalyseStackString();
    EXPECT_TRUE(stack->GetStackBalance() == expectedVal);
}

void SetAnalyseAndReturnBasement(Stack* stack, std::string str, int expectedVal) {
    stack->SetStackString(str);
    EXPECT_TRUE(stack->GetFirstBasementIndex() == 0);
    stack->AnalyseStackString();
    EXPECT_TRUE(stack->GetFirstBasementIndex() == expectedVal);
}

TEST(Day1Tests, Part1) {
    auto* stack = new Stack();
    ASSERT_TRUE(stack != nullptr);
    EXPECT_TRUE(stack->GetStackBalance() == 0);
    EXPECT_TRUE(stack->GetStackString() == "");

    SetAnalyseAndReturnBalance(stack, "(())", 0);
    SetAnalyseAndReturnBalance(stack, "()()", 0);

    SetAnalyseAndReturnBalance(stack, "(((", 3);
    SetAnalyseAndReturnBalance(stack, "(()(()(", 3);

    SetAnalyseAndReturnBalance(stack, "))(((((", 3);

    SetAnalyseAndReturnBalance(stack, "())", -1);
    SetAnalyseAndReturnBalance(stack, "))(", -1);

    SetAnalyseAndReturnBalance(stack, ")))", -3);
    SetAnalyseAndReturnBalance(stack, ")())())", -3);

    stack->SetStackString(str_testString);
    stack->AnalyseStackString();
    int stackBalance = stack->GetStackBalance();
    std::cout << stackBalance << std::endl;

    delete stack;
}

TEST(Day1Tests, Part2) {
    auto *stack = new Stack();
    ASSERT_TRUE(stack != nullptr);

    SetAnalyseAndReturnBasement(stack, ")", 1);
    SetAnalyseAndReturnBasement(stack, "()())", 5);

    stack->SetStackString(str_testString);
    stack->AnalyseStackString();
    int basementInd = stack->GetFirstBasementIndex();
    std::cout << basementInd << std::endl;

    delete stack;
}